mapper.delete:

delete from sys_role_menu
where role_id = #{roleId,jdbcType=BIGINT}
  and menu_id = #{menuId,jdbcType=BIGINT}

mapper.insert:

insert into sys_role_menu (role_id, menu_id, id,
  create_time, update_time, del_flag
  )
values (#{roleId,jdbcType=BIGINT}, #{menuId,jdbcType=BIGINT}, #{id,jdbcType=BIGINT},
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{isDeleted,jdbcType=TINYINT}
  )

mapper.update:

update sys_role_menu
set id = #{id,jdbcType=BIGINT},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  update_time = #{updateTime,jdbcType=TIMESTAMP},
  del_flag = #{isDeleted,jdbcType=TINYINT}
where role_id = #{roleId,jdbcType=BIGINT}
  and menu_id = #{menuId,jdbcType=BIGINT}

mapper.select:

select role_id, menu_id, id, create_time, update_time, del_flag
from sys_role_menu
where role_id = #{roleId,jdbcType=BIGINT}
  and menu_id = #{menuId,jdbcType=BIGINT}
